//def projectpath = groovyUtils.projectPath  

if   (projectpath.toLowerCase().contains("jenkins")) { 
	projectpath = projectpath + "\\"+ "DTI Automation Test 01\\src\\test\\soapui\\DTI"//Set the path of the project root from Slave

